mapper.select:
SELECT

FROM e_mail_template a
WHERE a.id = #{id}

SELECT

FROM e_mail_template a



		AND a.mt_type = #{mtType}




		ORDER BY ${page.orderBy}

mapper.update:
UPDATE e_mail_template SET
mt_type = #{mtType},
mt_content = #{mtContent},
pitch_on = #{pitchOn}
WHERE mt_id = #{mtId}

UPDATE e_mail_template SET
pitch_on = 1
WHERE mt_id = #{mtId}

UPDATE e_mail_template SET
pitch_on = 0
WHERE mt_type=#{mtType} and mt_id != #{mtId}
